import React, { Component } from 'react';
import Data from '../data'
const RoomContext = React.createContext();

class RoomProvider extends Component {
    state = {
        rooms: [],
        shortedRoom: [],
        featuredRoom: [],
        loading: true

    }
    componentDidMount() {
        let rooms = this.dataFormate(Data);
        let featuredRoom = rooms.filter(room => room.featured === true);
        this.setState = {
            rooms,
            featuredRoom,
            shortedRoom: rooms,
            loading: false
        }
    }
    dataFormate(Data) {
        let tempItem = Data.map((item) => {
            let id = item.sys.id
            let images = item.fields.images.map(image => image.fields.file.url);
            let room = { id, ...item.fields, images }
            return room;
        })
        return tempItem;
    }

    render() {
        return (
            <RoomContext.Provider value={ { ...this.state } }>
                { this.props.children }
            </RoomContext.Provider>
        );
    }
}

const RoomConsumar = RoomContext.Consumer;

export { RoomContext, RoomProvider, RoomConsumar };
