import React from 'react';
import { RoomConsumer } from './Context';
import Loading from './Loading'

const RoomContainer = () => {
    return (
        <RoomConsumer>
            {value => {
                const { loading, sortedRooms, rooms } = value;
                if (loading) {
                    return <Loading />
                }
            }
            }
        </RoomConsumer>
    );
};

export default RoomContainer;
